// Region trigger â†’ CON save (DC X). Fail = full 4d6 cold. Success = half.
// Shows BOTH rolls in chat.

const SAVE_DC = 15;          // <-- change this
const DMG_FORMULA = "4d6";
const DMG_TYPE = "cold";

if (!game.user.isActiveGM) return;

const tdoc = event?.data?.token ?? canvas.tokens.controlled?.[0]?.document;
const tokenObj = tdoc?.object ?? (tdoc?.id ? canvas.tokens.get(tdoc.id) : null);
const actor = tdoc?.actor ?? tokenObj?.actor;
if (!actor) return;

// --- CON save (visible roll) ---
const saveBonus = Number(actor.system?.abilities?.con?.save ?? actor.system?.abilities?.con?.mod ?? 0);
const saveFormula = `1d20${saveBonus >= 0 ? "+" : ""}${saveBonus}`;
const saveRoll = await (new Roll(saveFormula)).evaluate({ async: true });

await saveRoll.toMessage({
  speaker: ChatMessage.getSpeaker({ token: tokenObj }),
  flavor: `CON Save DC ${SAVE_DC}`
});

const success = Number(saveRoll.total ?? 0) >= SAVE_DC;

// --- Damage roll (visible roll) ---
const dmgRoll = await (new Roll(DMG_FORMULA)).evaluate({ async: true });
await dmgRoll.toMessage({
  speaker: ChatMessage.getSpeaker({ token: tokenObj }),
  flavor: `${DMG_FORMULA} ${DMG_TYPE} damage â€“ ${success ? "save success (half)" : "save fail (full)"}`
});

const full = Number(dmgRoll.total ?? 0);
const applied = success ? Math.floor(full / 2) : full;
if (!applied) return;

// --- Apply damage (Midi-QoL if present; otherwise raw HP subtraction) ---
if (globalThis.MidiQOL?.applyTokenDamage && tokenObj) {
  await MidiQOL.applyTokenDamage(
    [{ damage: applied, type: DMG_TYPE }],
    applied,
    new Set([tokenObj]),
    null,
    null
  );
  return;
}

// Raw HP subtraction fallback (no resistances/vulnerabilities).
const hp = actor.system.attributes.hp ?? {};
let temp = Number(hp.temp ?? 0);
let value = Number(hp.value ?? 0);

let remaining = applied;
if (temp > 0) {
  const spent = Math.min(temp, remaining);
  temp -= spent;
  remaining -= spent;
}
if (remaining > 0) value = Math.max(0, value - remaining);

await actor.update({
  "system.attributes.hp.temp": temp,
  "system.attributes.hp.value": value
});
