/**
 *
 * Teleport Source Token
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const ANIMATION1 = 'blfx.misc.spell.bubble1.implosion1.fire.above.reverse.orange';
const SCALE1 = 1.5; // Size of the animation. Default value: 1.5
const WAIT_UNTIL_FINISHED1 = -600; // Default value: -600
const WAIT_UNTIL_FINISHED_ENABLED1 = true;
const ENABLED1 = true;
const SOUND_ENABLED1 = true;
const SOUND1 = 'blfx.sound.spell.cast.buff.1';
const DELAY_SOUND1 = 0; // Default value: 0
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION3 = 'blfx.misc.spell.bubble1.explosion1.fire.above.orange';
const SCALE3 = 1.6; // Size of the animation. Default value: 1.5
const WAIT_UNTIL_FINISHED3 = -900; // Default value: -900
const WAIT_UNTIL_FINISHED_ENABLED3 = true;
const ENABLED3 = true;
const SOUND_ENABLED3 = false;
const SOUND3 = 'blfx.sound.spell.cast.buff.1';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;
//
// ------------------------------------------------

if (typeof sourceToken === 'undefined' || sourceToken === null) return;

const range = item.system.range?.value ?? 30; // from the item
const targetDest = await Sequencer.Crosshair.show({
  icon: {
    texture: sourceToken.document.texture.src,
    borderVisible: false,
  },
  location: {
    obj: sourceToken,
    showRange: true,
    wallBehavior: Sequencer.Crosshair.PLACEMENT_RESTRICTIONS.LINE_OF_SIGHT,
    limitMaxRange: range,
    displayRangePoly: false,
  },
});

new Sequence()

  // ON SOURCE TOKEN
  .sound()
  .playIf(SOUND_ENABLED1)
  .file(SOUND1)
  .delay(DELAY_SOUND1)
  .startTime(START_TIME_SOUND1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

  .effect()
  .playIf(ENABLED1)
  .file(ANIMATION1)
  .atLocation(sourceToken)
  .scaleToObject(SCALE1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])

  // FADE-OUT
  .animation()
  .on(sourceToken)
  .opacity(0)
  .fadeOut(500)
  .waitUntilFinished()

  // TELEPORT
  .animation()
  .on(sourceToken)
  .teleportTo(targetDest)
  .snapToGrid()
  .waitUntilFinished()

  // ON TARGET
  .sound()
  .playIf(SOUND_ENABLED3)
  .file(SOUND3)
  .delay(DELAY_SOUND3)
  .startTime(START_TIME_SOUND3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

  .effect()
  .playIf(ENABLED3)
  .file(ANIMATION3)
  .atLocation(targetDest)
  .scaleToObject(SCALE3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

  // FADE-IN
  .animation()
  .on(sourceToken)
  .opacity(1)
  .fadeIn(500)
  .waitUntilFinished()

  .play();

