const DELAY_SOUND1 = 400; // Default value: 400
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION2 = 'jb2a.spear.melee.fire.red';
const SCALE2 = 0.5; // Size of the animation. Default value: 0.5
const OFFSET2 = 0.5; // Default value: 0.5
const WAIT_UNTIL_FINISHED2 = -600; // Default value: -600
const WAIT_UNTIL_FINISHED_ENABLED2 = true;
const ENABLED2 = false;
const SOUND_ENABLED2 = false;
const SOUND2 = 'psfx.weapon-attacks.spear.v1.4';
const DELAY_SOUND2 = 0; // Default value: 900
const START_TIME_SOUND2 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED2 = false;
const WAIT_UNTIL_FINISHED_SOUND2 = 0;

const ANIMATION3 = 'animated-spell-effects-cartoon.fire.37';
const SCALE3 = 2; // Size of the animation. Default value: 2
const OFFSET3 = 0; // Default value: 0
const FADEIN3 = 0;
const FADEOUT3 = 0;
const BELOW_TOKENS3 = false;
const PLAY_ANIMATION_IF3 = 'hit'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
const WAIT_UNTIL_FINISHED3 = -200; // Default value: -200
const WAIT_UNTIL_FINISHED_ENABLED3 = false;
const ENABLED3 = true;
const SOUND_ENABLED3 = true;
const SOUND3 = 'modules/psfx-patreon/library/weapon-swooshes/fire/v1/group01/meleeattack-swoosh-magicaleffect-group01-fire-00.ogg';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const PLAY_SOUND_IF3 = 'hit'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;
const SHAKE_ENABLED3 = true;
const SHAKE_DURATION3 = 2000;
const SHAKE_INTENSITY3 = 0.08;
const PLAY_SHAKE_IF3 = 'critical hit'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
//
// ------------------------------------------------

const myToken = sourceToken;
const [targetToken] = targetTokens;

if (!targetToken) return;
const targetWidth = targetToken.document.width;
const is5ftAttack = blfx.helpers.edgesIntersect(myToken, targetToken);
const elevation = targetToken.document.elevation;
const scale = myToken.document.width > 1 ? SCALE2 * 1.4 : SCALE2;
const offset2 = myToken.document.width > 1 && !is5ftAttack ? (myToken.document.width * OFFSET2) / 2 : myToken.document.width * OFFSET2;
const offset3 = targetWidth > 1 && !is5ftAttack ? (targetWidth * OFFSET3) / 2 : targetWidth * OFFSET3;
const animationFile = is5ftAttack ? ANIMATION2 : blfx.helpers.get10ftAnimation(ANIMATION2);
const mirror = blfx.helpers.isTokenOnLeft(myToken, targetToken);
const playAnimationIf3 = blfx.helpers.getPlayIfValue(PLAY_ANIMATION_IF3, roll, targetToken);
const playSoundIf3 = blfx.helpers.getPlayIfValue(PLAY_SOUND_IF3, roll, targetToken);
const playShakeIf3 = blfx.helpers.getPlayIfValue(PLAY_SHAKE_IF3, roll, targetToken);
const angle3 = blfx.helpers.calculateAngle(myToken.center, targetToken.center);
const isTargetFlipped = blfx.helpers.isTokenFlipped(targetToken);

await new Sequence()

  // ON SOURCE TOKEN
  .sound()
  .playIf(SOUND_ENABLED1)
  .file(SOUND1)
  .delay(DELAY_SOUND1)
  .startTime(START_TIME_SOUND1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

  .effect()
  .playIf(ENABLED1)
  .atLocation(myToken)
  .file(ANIMATION1)
  .scaleToObject(SCALE1)
  .fadeIn(FADEIN1)
  .fadeOut(FADEOUT1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])

  // FROM SOURCE TO TARGET
  .sound()
  .playIf(SOUND_ENABLED2)
  .file(SOUND2)
  .delay(DELAY_SOUND2)
  .startTime(START_TIME_SOUND2)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED2, WAIT_UNTIL_FINISHED_SOUND2])

  .effect()
  .playIf(ENABLED2)
  .file(animationFile)
  .zIndex(100)
  .atLocation(myToken, { gridUnits: true })
  .rotateTowards(targetToken, { gridUnits: true })
  .center()
  .spriteOffset({ x: offset2 }, { gridUnits: true })
  .scale(scale)
  .elevation(elevation)
  .mirrorY(mirror)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED2, WAIT_UNTIL_FINISHED2])

  // ON TARGET
  .sound()
  .playIf(SOUND_ENABLED3 && playSoundIf3)
  .file(SOUND3)
  .delay(DELAY_SOUND3)
  .startTime(START_TIME_SOUND3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

  .effect()
  .playIf(ENABLED3 && playAnimationIf3)
  .file(ANIMATION3)
  .zIndex(50)
  .atLocation(targetToken)
  .rotateTowards(targetToken, { rotationOffset: angle3, gridUnits: true })
  .center()
  .spriteOffset({ x: offset3 }, { gridUnits: true })
  .elevation(elevation, { absolute: true })
  .belowTokens(BELOW_TOKENS3)
  .scaleToObject(SCALE3)
  .fadeIn(FADEIN3)
  .fadeOut(FADEOUT3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

  // SHAKE
  .effect()
  .playIf(SHAKE_ENABLED3 && playShakeIf3)
  .copySprite(targetToken)
  .mirrorY(isTargetFlipped)
  .zIndex(0)
  .loopProperty('sprite', 'position.x', {
    from: -SHAKE_INTENSITY3,
    to: SHAKE_INTENSITY3,
    duration: 60,
    pingPong: true,
    delay: 0,
    gridUnits: true,
  })
  .loopProperty('sprite', 'position.y', {
    from: -SHAKE_INTENSITY3,
    to: SHAKE_INTENSITY3,
    duration: 15,
    pingPong: true,
    delay: 0,
    gridUnits: true,
  })
  .scaleToObject(targetToken.document.texture.scaleX)
  .duration(SHAKE_DURATION3)

  .play();

