const offset2 = myToken.document.width > 1 && !is5ftAttack ? (myToken.document.width * OFFSET2) / 2 : myToken.document.width * OFFSET2;
const offset3 = targetWidth > 1 && !is5ftAttack ? (targetWidth * OFFSET3) / 2 : targetWidth * OFFSET3;
const animationFile = is5ftAttack ? ANIMATION2 : blfx.helpers.get10ftAnimation(ANIMATION2);
const mirror = blfx.helpers.isTokenOnLeft(myToken, targetToken);
const playAnimationIf3 = blfx.helpers.getPlayIfValue(PLAY_ANIMATION_IF3, roll, targetToken);
const playSoundIf3 = blfx.helpers.getPlayIfValue(PLAY_SOUND_IF3, roll, targetToken);
const playShakeIf3 = blfx.helpers.getPlayIfValue(PLAY_SHAKE_IF3, roll, targetToken);
const angle3 = blfx.helpers.calculateAngle(myToken.center, targetToken.center);
const isTargetFlipped = blfx.helpers.isTokenFlipped(targetToken);

await new Sequence()

  // ON SOURCE TOKEN
  .sound()
  .playIf(SOUND_ENABLED1)
  .file(SOUND1)
  .delay(DELAY_SOUND1)
  .startTime(START_TIME_SOUND1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

  .effect()
  .playIf(ENABLED1)
  .atLocation(myToken)
  .file(ANIMATION1)
  .scaleToObject(SCALE1)
  .fadeIn(FADEIN1)
  .fadeOut(FADEOUT1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])

  // FROM SOURCE TO TARGET
  .sound()
  .playIf(SOUND_ENABLED2)
  .file(SOUND2)
  .delay(DELAY_SOUND2)
  .startTime(START_TIME_SOUND2)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED2, WAIT_UNTIL_FINISHED_SOUND2])

  .effect()
  .playIf(ENABLED2)
  .file(animationFile)
  .zIndex(100)
  .atLocation(myToken, { gridUnits: true })
  .rotateTowards(targetToken, { gridUnits: true })
  .center()
  .spriteOffset({ x: offset2 }, { gridUnits: true })
  .scale(scale)
  .elevation(elevation)
  .mirrorY(mirror)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED2, WAIT_UNTIL_FINISHED2])

  // ON TARGET
  .sound()
  .playIf(SOUND_ENABLED3 && playSoundIf3)
  .file(SOUND3)
  .delay(DELAY_SOUND3)
  .startTime(START_TIME_SOUND3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

  .effect()
  .playIf(ENABLED3 && playAnimationIf3)
  .file(ANIMATION3)
  .zIndex(50)
  .atLocation(targetToken)
  .rotateTowards(targetToken, { rotationOffset: angle3, gridUnits: true })
  .center()
  .spriteOffset({ x: offset3 }, { gridUnits: true })
  .elevation(elevation, { absolute: true })
  .belowTokens(BELOW_TOKENS3)
  .scaleToObject(SCALE3)
  .fadeIn(FADEIN3)
  .fadeOut(FADEOUT3)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

  // SHAKE
  .effect()
  .playIf(SHAKE_ENABLED3 && playShakeIf3)
  .copySprite(targetToken)
  .mirrorY(isTargetFlipped)
  .zIndex(0)
  .loopProperty('sprite', 'position.x', {
    from: -SHAKE_INTENSITY3,
    to: SHAKE_INTENSITY3,
    duration: 60,
    pingPong: true,
    delay: 0,
    gridUnits: true,
  })
  .loopProperty('sprite', 'position.y', {
    from: -SHAKE_INTENSITY3,
    to: SHAKE_INTENSITY3,
    duration: 15,
    pingPong: true,
    delay: 0,
    gridUnits: true,
  })
  .scaleToObject(targetToken.document.texture.scaleX)
  .duration(SHAKE_DURATION3)

  .play();

