//Last Updated: 9/14/2023
//Author: EskieMoh#2969

//Set torch or lantern
let lightSource = "torch"

if (lightSource == "torch"){
file = "https://i.imgur.com/1U1eb9t.png"
}  
else if (lightSource == "lantern"){
file = "https://i.imgur.com/3wFQD02.png"
}

let lightDim = token.document.light.dim;
let lightBright = token.document.light.bright;

if (lightDim == 0 && lightBright == 0){
new Sequence()

.effect()
.delay(150)
.name(`${token.document.name} ${lightSource}`)
.file("jb2a.impact.002.orange")
.atLocation(token)
.attachTo(token, {followRotation: true, local: true})
.scaleToObject(0.9, {considerTokenScale: true})
.spriteOffset({x:0.525*token.document.width, y:-0.05*token.document.width}, {gridUnits:true})
.spriteRotation(45)
.zIndex(0.1)
.playIf(() => {
return lightSource == "torch";
})

.effect()
.name(`${token.document.name} ${lightSource}`)
.file(file)
.atLocation(token)
.attachTo(token, {followRotation: true, local: true})
.scaleToObject(1.2, {considerTokenScale: true})
.scaleIn(0, 500, {ease: "easeOutElastic"})
.scaleOut(0, 250, {ease: "easeOutCubic"})
.spriteOffset({x:0.35*token.document.width, y:0.1*token.document.width}, {gridUnits:true})
.animateProperty("sprite", "rotation", { from: 15, to: -15, duration: 300, ease: "easeInOutBack"})
.animateProperty("sprite", "rotation", { from: 0, to: 30, duration: 250, delay: 200, ease: "easeOutBack"})
.persist()
.zeroSpriteRotation()
.playIf(() => {
return lightSource == "torch";
})

.effect()
.delay(250)
.name(`${token.document.name} ${lightSource}`)
.file("jb2a.flames.01.orange")
.atLocation(token)
.attachTo(token, {followRotation: true, local: true})
.scaleToObject(1, {considerTokenScale: true})
.spriteOffset({x:0.5*token.document.width, y:-0.05*token.document.width}, {gridUnits:true})
.persist()
.spriteRotation(45)
.zIndex(0.1)
.waitUntilFinished(-500)
.playIf(() => {
return lightSource == "torch";
})

.effect()
.name(`${token.document.name} ${lightSource}`)
.file(file)
.atLocation(token)
.attachTo(token, {followRotation: true, local: true})
.scaleToObject(1, {considerTokenScale: true})
.scaleIn(0, 500, {ease: "easeOutElastic"})
.scaleOut(0, 250, {ease: "easeOutCubic"})
.spriteOffset({x:0.35*token.document.width, y:0.1*token.document.width}, {gridUnits:true})
.animateProperty("sprite", "rotation", { from: 15, to: -15, duration: 300, ease: "easeInOutBack"})
.animateProperty("sprite", "rotation", { from: 0, to: 30, duration: 250, delay: 200, ease: "easeOutBack"})
.loopProperty("sprite", "rotation", { from: 3, to: -3, duration: 1500, ease: "easeOutQuad", pingPong: true })
.persist()
.zeroSpriteRotation()
.waitUntilFinished(-500)
.playIf(() => {
return lightSource == "lantern";
})

.thenDo(function(){
var light = {dim: 0, bright: 0};
token.document.update({light});

Sequencer.EffectManager.endEffects({ name: `${token.document.name} ${lightSource}`, object: token });
})

.play()

//Define token light options
var light = {dim: 30, bright: 15, alpha:0.25, luminosity: 0.5, color: "#ffb433", animation: {type: "torch", speed: 4, intensity: 4},attenuation: 0.75, contrast:0.15, shadows:0.2,};

await token.document.update({light});

} else {

await Sequencer.EffectManager.endEffects({ name: `${token.document.name} ${lightSource}`, object: token });

var light = {dim: 0, bright: 0};
await token.document.update({light});
}

attackFireBoltII.command:
/**
 *
 * Attack Ranged
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const ANIMATION1 = 'blfx.spell.cast.swirl1.fire1.orange';
const SCALE1 = 1.5; // Default value: 1.5
const OFFSET1 = 0; // Default value: 0
const FADEIN1 = 0;
const FADEOUT1 = 0;
const WAIT_UNTIL_FINISHED1 = -500; // Default value: -500
const WAIT_UNTIL_FINISHED_ENABLED1 = true;
const ENABLED1 = true;
const SOUND_ENABLED1 = true;
const SOUND1 = 'psfx.cantrips.fire-bolt.v1';
const DELAY_SOUND1 = 900; // Default value: 900
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION2 = 'autoanimations.range.spell.firebolt.01.orange';
const WAIT_UNTIL_FINISHED2 = -500; // Default value: -500
const WAIT_UNTIL_FINISHED_ENABLED2 = false;
const BELOW_TOKENS2 = false;
const MISS_ANIMATION2 = true;
const ENABLED2 = true;
const SOUND_ENABLED2 = false;
const SOUND2 = 'blfx.sound.weapon.range.arrow.hit.attack2';
const DELAY_SOUND2 = 0; // Default value: 0
const START_TIME_SOUND2 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED2 = false;
const WAIT_UNTIL_FINISHED_SOUND2 = 0;

const ANIMATION3 = 'blfx.spell.template.circle.crack2.ground1.orange';
const SCALE3 = 2; // Size of the animation. Default value: 2
const OFFSET3 = 0; // Default value: 0
const FADEIN3 = 0;
const FADEOUT3 = 0;
const BELOW_TOKENS3 = false;
const PLAY_ANIMATION_IF3 = 'always'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
const WAIT_UNTIL_FINISHED3 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_ENABLED3 = false;
const ENABLED3 = false;
const SOUND_ENABLED3 = false;
const SOUND3 = 'blfx.sound.spell.cast.entangle.roots.1';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const PLAY_SOUND_IF3 = 'always'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;
const SHAKE_ENABLED3 = false;
const SHAKE_DURATION3 = 400;
const SHAKE_INTENSITY3 = 0.08;
const PLAY_SHAKE_IF3 = 'always'; // Possible values: 'always', 'hit', 'critical hit', 'miss', 'critical miss'
//
// ------------------------------------------------

const myToken = sourceToken;
const targets = targetTokens;
const sourceElevation = myToken.document.elevation;
const offset1 = OFFSET1 * myToken.document.width;

for (let targetToken of targets) {
  const targetElevation = targetToken.document.elevation;
  const playAnimationIf3 = blfx.helpers.getPlayIfValue(PLAY_ANIMATION_IF3, roll, targetToken);
  const playSoundIf3 = blfx.helpers.getPlayIfValue(PLAY_SOUND_IF3, roll, targetToken);
  const playShakeIf3 = blfx.helpers.getPlayIfValue(PLAY_SHAKE_IF3, roll, targetToken);
  const isHit = blfx.helpers.isTargetHit(roll, targetToken);
  const angle3 = blfx.helpers.calculateAngle(myToken.center, targetToken.center);
  const offset3 = targetToken.document.width * OFFSET3;
  const isTargetFlipped = blfx.helpers.isTokenFlipped(targetToken);

  let impactPoint = targetToken.center;
  if (!isHit && MISS_ANIMATION2) {
    impactPoint = blfx.helpers.getTokenCorner(myToken, targetToken);
  }

  await new Sequence()

    // ON SOURCE TOKEN
    .sound()
    .playIf(SOUND_ENABLED1)
    .file(SOUND1)
    .delay(DELAY_SOUND1)
    .startTime(START_TIME_SOUND1)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

    .effect()
    .playIf(ENABLED1)
    .file(ANIMATION1)
    .elevation(sourceElevation)
    .atLocation(myToken)
    .rotateTowards(impactPoint)
    .scaleToObject(SCALE1)
    .fadeIn(FADEIN1)
    .fadeOut(FADEOUT1)
    .center()
    .spriteOffset({ x: offset1 }, { gridUnits: true })
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])

    // FROM SOURCE TO TARGET
    .sound()
    .playIf(SOUND_ENABLED2)
    .file(SOUND2)
    .delay(DELAY_SOUND2)
    .startTime(START_TIME_SOUND2)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED2, WAIT_UNTIL_FINISHED_SOUND2])

    .effect()
    .playIf(ENABLED2)
    .file(ANIMATION2)
    .zIndex(100)
    .elevation(targetElevation)
    .atLocation(myToken)
    .stretchTo(impactPoint)
    .belowTokens(BELOW_TOKENS2)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED2, WAIT_UNTIL_FINISHED2])

    // ON TARGET
    .sound()
    .playIf(SOUND_ENABLED3 && playSoundIf3)
    .file(SOUND3)
    .delay(DELAY_SOUND3)
    .startTime(START_TIME_SOUND3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

    .effect()
    .playIf(ENABLED3 && playAnimationIf3)
    .file(ANIMATION3)
    .zIndex(50)
    .atLocation(targetToken)
    .rotateTowards(targetToken, { rotationOffset: angle3, gridUnits: true })
    .center()
    .spriteOffset({ x: offset3 }, { gridUnits: true })
    .elevation(targetElevation, { absolute: true })
    .belowTokens(BELOW_TOKENS3)
    .scaleToObject(SCALE3)
    .fadeIn(FADEIN3)
    .fadeOut(FADEOUT3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

    // SHAKE
    .effect()
    .playIf(SHAKE_ENABLED3 && playShakeIf3)
    .copySprite(targetToken)
    .mirrorY(isTargetFlipped)
    .zIndex(0)
    .loopProperty('sprite', 'position.x', {
      from: -SHAKE_INTENSITY3,
      to: SHAKE_INTENSITY3,
      duration: 60,
      pingPong: true,
      delay: 0,
      gridUnits: true,
    })
    .loopProperty('sprite', 'position.y', {
      from: -SHAKE_INTENSITY3,
      to: SHAKE_INTENSITY3,
      duration: 15,
      pingPong: true,
      delay: 0,
      gridUnits: true,
    })
    .scaleToObject(targetToken.document.texture.scaleX)
    .duration(SHAKE_DURATION3)

    .play();
}

macro.command:
const dim = 40;
const bright = 20;
const color = undefined; // game.user.color;
const alpha = "0.25";
const type = "torch";
const speed = 3;
const intensity = 1;
const angle = 360;

const lightOptions = {dim,bright,color,alpha,type,speed,intensity,angle};

if (!game.modules.get("warpgate")?.active) return ui.notifications.error("Please enable the Warp Gate module");
const mutName = "Cantrip: Light";

if (args[0].macroPass === "preActiveEffects") {
    const {failedSaves} = args[0];
    if (!actor || !token || !failedSaves) return ui.notifications.error("Light spell error - Do you have Item Macro Sheet hooks option enabled? If yes disable; Notify GM");
    let {dim,bright,color,alpha,type,speed,intensity,angle} = lightOptions ?? {};
    const previousTargetUuid = DAE.getFlag(actor, "lightSRD");
    const previousTokenDoc = previousTargetUuid ? fromUuidSync(previousTargetUuid) : undefined;
    const effect = previousTokenDoc ? getEffect(previousTokenDoc.actor,"Light") : undefined;
    if (previousTargetUuid && effect) await MidiQOL.socket().executeAsGM("removeEffects", {actorUuid: previousTargetUuid, effects:[effect.id]});
    if (previousTokenDoc && _hasMutation(previousTokenDoc,mutName)) await warpgate.revert(previousTokenDoc,mutName);
    await warpgate.wait(350);
    if (!failedSaves.length) return ui.notifications.info("Light spell resisted");
    let content = "";
    let optionUpdates = "";
    if (!type) {
        const typeOptions = [['none',{label:'None'}]]
                            .concat(Object.entries(CONFIG.Canvas.lightAnimations))
                            .reduce((acc, [a,b]) => acc += `<option id="type" value="${a}">${game.i18n.localize(b.label)}</option>`, ``);
        content += `<div class="form-group"><label>Light Animation Type</label><div class="form-fields"><select id="type">${typeOptions}</select></div></div> `;
    };
    if (!color) content += `<div class="form-group"><label for="color" style="line-height: 26px;">Color:</label><div class="form-fields"><input type="color" value="${game.user.color}" id="color"></div></div>`;
    if (!alpha) content += `<div class="form-group"><label for="alpha" style="line-height: 26px;">Color Intensity (Alpha):</label><div class="form-fields"><input type="range" value="0.25" id="alpha" min="0" max="1" step="0.05"><span class="range-value" id="alphaOutput">0.25</span></div><p class="hint">Configure the intensity of the light source.</p></div>`;
    if (!dim) content += `<div class="form-group"><label for="dim" style="line-height: 26px;">Dim (ft):</label><div class="form-fields"><input type="range" value="40" id="dim" min="0" max="120" step="5"><span class="range-value" id="dimOutput">20</span></div></div>`;
    if (!bright) content += `<div class="form-group"><label for="bright" style="line-height: 26px;">Bright (ft):</label><div class="form-fields"><input type="range" value="20" id="bright" min="0" max="120" step="5"><span class="range-value" id="brightOutput">20</span></div></div>`;
    if (!angle) content += `<div class="form-group"><label for="angle" style="line-height: 26px;">Emission Angle (Degrees):</label><div class="form-fields"><input type="range" value="360" id="angle" min="0" max="360" step="1"><span class="range-value" id="angleOutput">360</span></div><p class="hint">Configure the angle of the light source.</p></div>`;
    if (!intensity) content += `<div class="form-group"><label for="intensity" style="line-height: 26px;">Animation Intensity:</label><div class="form-fields"><input type="range" value="1" id="intensity" min="0" max="10" step="1"><span class="range-value" id="intensityOutput">1</span></div><p class="hint">Configure the intesity of the light source's animation.</p></div>`;
    if (!speed) content += `<div class="form-group"><label for="speed" style="line-height: 26px;">Animation Speed:</label><div class="form-fields"><input type="range" value="3" id="speed" min="0" max="10" step="1"><span class="range-value" id="speedOutput">3</span></div><p class="hint">Configure the speed of the light source's animation.</p></div>`;
    if (content) {
        optionUpdates = await Dialog.wait({
            title: "Light configuration",
            content: `<form>${content}</form>`,
            buttons: getLights(),
            close: () => {return false},
            render: () => {
                const valueAlpha = document.querySelector("#alphaOutput");
                const inputAlpha = document.querySelector("#alpha");
                if (valueAlpha && inputAlpha) {
                    valueAlpha.textContent = inputAlpha.value;
                    inputAlpha.addEventListener("input", (event) => {valueAlpha.textContent = event.target.value});
                }
                const valueDim = document.querySelector("#dimOutput");
                const inputDim = document.querySelector("#dim");
                if (valueDim && inputDim) {
                	valueDim.textContent = inputDim.value;
                	inputDim.addEventListener("input", (event) => {valueDim.textContent = event.target.value});
                }
                const valueBright = document.querySelector("#brightOutput");
                const inputBright = document.querySelector("#bright");
                if (valueBright && inputBright) {
                	valueBright.textContent = inputBright.value;
                	inputBright.addEventListener("input", (event) => {valueBright.textContent = event.target.value});
                }
                const valueAngle = document.querySelector("#angleOutput");
                const inputAngle = document.querySelector("#angle");
                if (valueAngle && inputAngle) {
                	valueAngle.textContent = inputAngle.value;
                	inputAngle.addEventListener("input", (event) => {valueAngle.textContent = event.target.value});
                }
                const valueSpeed = document.querySelector("#speedOutput");
                const inputSpeed = document.querySelector("#speed");
                if (valueSpeed && inputSpeed) {
                	valueSpeed.textContent = inputSpeed.value;
                	inputSpeed.addEventListener("input", (event) => {valueSpeed.textContent = event.target.value});
                }
                const valueIntensity = document.querySelector("#intensityOutput");
                const inputIntensity = document.querySelector("#intensity");
                if (valueIntensity && inputIntensity) {
                	valueIntensity.textContent = inputIntensity.value;
                	inputIntensity.addEventListener("input", (event) => {valueIntensity.textContent = event.target.value});
                }
            }
        })
    }
    let updates;
    if (!optionUpdates) {
        Hooks.once("midi-qol.preApplyDynamicEffects", workflow =>{return false})
        return ui.notifications.info("Light spell casting cancelled");
    }
    if (!!optionUpdates) updates = { token: { light: optionUpdates } };
    else updates = { token: { light: { color, alpha, dim, bright, angle, animation:{ speed, intensity, type } } } };
    const target = failedSaves[0]; //Token5e#Document
    if (_hasMutation(target,mutName)) {
        await MidiQOL.socket().executeAsGM("removeEffects", {actorUuid: target.uuid, effects:[getEffect(target.actor,"Light")?.id]});
        await warpgate.revert(target,mutName);
    }
    await warpgate.mutate(target,updates,{},{name:mutName});
    await DAE.setFlag(actor, "lightSRD", target.uuid);
}
if (args[0] === "off") {
    const sourceActor = fromUuidSync(lastArgValue.origin)?.actor;
    if (_hasMutation(token,mutName)) await warpgate.revert(token.document,mutName);
    await DAE.unsetFlag(sourceActor, "lightSRD");
}


function _hasMutation(token,mutName) {
    const tokenDoc = token instanceof TokenDocument ? token : token.document;
    const stack = warpgate.mutationStack(tokenDoc);
    return !!stack.getName(mutName);
}

function getLights() {
    let lightButtons = {};
    lightButtons = Object.assign(lightButtons, {
        apply: {
            label: `Apply`,
            icon: "<i class='fa-solid fa-lightbulb'></i>",
            callback: (html) => {
                const newColor = document.querySelector("#color")?.value ?? color;
                const newAlpha = document.querySelector("#alpha")?.value ?? alpha;
                const newDim = document.querySelector("#dim")?.value ?? dim;
                const newBright = document.querySelector("#bright")?.value ?? bright;
                const newAngle = document.querySelector("#angle")?.value ?? angle;
                const newType = document.querySelector("#type")?.value ?? type;
                const newIntensity = document.querySelector("#intensity")?.value ?? intensity;
                const newSpeed = document.querySelector("#speed")?.value ?? speed;
                return {color:newColor, alpha:newAlpha, dim:newDim, bright:newBright, angle:newAngle, animation:{type:newType, instensity:newIntensity, speed:newSpeed}};
            }
        }
    })
    return lightButtons;
}

function getEffect(actor, effectName) {
    let effect;
    if(isNewerVersion(game.version, 11)) effect = actor.effects.getName(effectName);
    else effect = actor.effects.find(e=>e.label === effectName);
    return effect;
}

savePhantaForceI.command:
/**
 *
 * On Target or Token
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const ANIMATION1 = 'blfx.spell.cast.swirl1.magic1.light_wheels.2.color3';
const SCALE1 = 2; // Size of the animation. Default value: 2
const FADEIN1 = 0;
const FADEOUT1 = 0;
const WAIT_UNTIL_FINISHED1 = -700; // Default value: -700
const WAIT_UNTIL_FINISHED_ENABLED1 = true;
const BELOW_TOKENS1 = false;
const ENABLED1 = true;
const SOUND_ENABLED1 = true;
const SOUND1 = 'psfx.magic-signs.circle.v1.illusion.outro';
const DELAY_SOUND1 = 0; // Default value: 0
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION2 = 'blfx.spell.range.ray.beam1.sinusoidal.intro.orange';
const WAIT_UNTIL_FINISHED2 = -100; // Default value: -100
const WAIT_UNTIL_FINISHED_ENABLED2 = true;
const ENABLED2 = false;
const SOUND_ENABLED2 = false;
const SOUND2 = 'blfx.sound.spell.cast.generic.1';
const DELAY_SOUND2 = 0; // Default value: 0
const START_TIME_SOUND2 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED2 = false;
const WAIT_UNTIL_FINISHED_SOUND2 = 0;

const ANIMATION3 = 'blfx.spell.cast.swirl1.magic1.light_wheels.2.color3';
const SCALE3 = 2; // Size of the animation. Default value: 2
const FADEIN3 = 0;
const FADEOUT3 = 0;
const BELOW_TOKENS3 = false;
const WAIT_UNTIL_FINISHED3 = -700; // Default value: -700
const WAIT_UNTIL_FINISHED_ENABLED3 = true;
const ENABLED3 = true;
const SOUND_ENABLED3 = true;
const SOUND3 = 'psfx.magic-signs.circle.v1.illusion.outro';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;
const SHAKE_ENABLED3 = false;
const SHAKE_DURATION3 = 400;
const SHAKE_INTENSITY3 = 0.08;
//
// ------------------------------------------------

if (typeof targetTokens === 'undefined' || targetTokens === null) return;

const controlledToken = sourceToken;
const sourceElevation = controlledToken.document.elevation;

// ON SOURCE TOKEN
await new Sequence()
  .sound()
  .playIf(SOUND_ENABLED1)
  .file(SOUND1)
  .delay(DELAY_SOUND1)
  .startTime(START_TIME_SOUND1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

  .effect()
  .playIf(ENABLED1)
  .file(ANIMATION1)
  .atLocation(controlledToken)
  .scaleToObject(SCALE1)
  .fadeIn(FADEIN1)
  .fadeOut(FADEOUT1)
  .elevation(sourceElevation, { absolute: false })
  .belowTokens(BELOW_TOKENS1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])
  .play();

for (let targetToken of targetTokens) {
  const targetElevation = targetToken.document?.elevation ?? 0;
  const scaleX = targetToken.document?.texture?.scaleX ?? 1;
  const isTargetFlipped = blfx.helpers.isTokenFlipped(targetToken);

  // FROM SOURCE TO TARGET
  await new Sequence()
    .sound()
    .playIf(SOUND_ENABLED2)
    .file(SOUND2)
    .delay(DELAY_SOUND2)
    .startTime(START_TIME_SOUND2)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED2, WAIT_UNTIL_FINISHED_SOUND2])

    .effect()
    .playIf(ENABLED2)
    .file(ANIMATION2)
    .zIndex(100)
    .elevation(targetElevation)
    .atLocation(controlledToken)
    .stretchTo(targetToken)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED2, WAIT_UNTIL_FINISHED2])

    // ON TARGET
    .sound()
    .playIf(SOUND_ENABLED3)
    .file(SOUND3)
    .delay(DELAY_SOUND3)
    .startTime(START_TIME_SOUND3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

    .effect()
    .playIf(ENABLED3)
    .file(ANIMATION3)
    .zIndex(50)
    .atLocation(targetToken)
    .scaleToObject(SCALE3)
    .fadeIn(FADEIN3)
    .fadeOut(FADEOUT3)
    .elevation(targetElevation, { absolute: false })
    .belowTokens(BELOW_TOKENS3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

    // SHAKE
    .effect()
    .playIf(SHAKE_ENABLED3)
    .copySprite(targetToken)
    .mirrorY(isTargetFlipped)
    .zIndex(0)
