macroData.command:
// ItemMacro â€” On Use (Post Active Effects). Requires DAE + Midi-QoL + Advanced Macros + Item Macro.
const lastArg = args.at(-1);

// The item that was just used
const item = await fromUuid(lastArg.itemUuid);

// Resolve the targets robustly across Midi-QoL versions
let targetUuids = lastArg.targetUuids ?? lastArg.targets ?? [];
if (!Array.isArray(targetUuids)) targetUuids = [targetUuids];
const targets = targetUuids
  .map(u => {
    const obj = fromUuidSync(u);
    return obj?.object ?? (obj?.actor?.getActiveTokens?.()[0] ?? null);
  })
  .filter(Boolean);

// We want a TOTAL of 5 stacks. Midi-QoL already applied 1 stack.
// Add the remaining 4.
const TOTAL_STACKS = 4;
const extra = Math.max(0, TOTAL_STACKS - 1);

for (let i = 0; i < extra; i++) {
  await DAE.doEffects(item, true, targets);
}

// ItemMacro â€” On Use (Post Active Effects)
// Consumes exactly 1 stack from a SELF effect each time this item is used.
// Requires: DAE + Midi-QoL + Item Macro (Advanced Macros recommended).

const lastArg = args.at(-1);
const usedItem = await fromUuid(lastArg.itemUuid);

// --- CONFIG: set to your stacked effect's label on the USER ---
const EFFECT_LABEL = "Shield of Missiles"; // â† exact effect label
// --------------------------------------------------------------

// Resolve SELF actor robustly
let actorDoc = null;
if (lastArg.actorUuid) actorDoc = await fromUuid(lastArg.actorUuid);
if (!actorDoc && lastArg.tokenId) {
  const tok = canvas.tokens.get(lastArg.tokenId);
  actorDoc = tok?.actor ?? null;
}
if (!actorDoc) actorDoc = game.user?.character ?? null;
if (!actorDoc) return ui.notifications.error("Consume Stack: no actor to operate on.");

// Find the stacked effect on SELF: prefer label, fallback to origin (same item)
let eff = [...actorDoc.effects].find(e =>
  (e.label ?? e.name) === EFFECT_LABEL &&
  (e.getFlag("dae", "stackable") === "count" || e.flags?.dae?.stackable === "count")
);

if (!eff) {
  // Fallback: if the same item originally applied the stacks, match by origin
  eff = [...actorDoc.effects].find(e => e.origin === usedItem.uuid);
}

if (!eff) {
  ui.notifications.info(`No count-stack effect '${EFFECT_LABEL}' on ${actorDoc.name}.`);
  return;
}

// If decrement-on-delete is set, delete = minus one (auto-removes at 0)
if (eff.getFlag("dae", "decrementDelete")) {
  await eff.delete();
  ChatMessage.create({ content: `âž– Consumed 1 '${eff.label}' on ${actorDoc.name}.` });
} else {
  // Manual decrement fallback (handles older/newer DAE flag keys)
  let cur =
    eff.getFlag("dae", "stacks") ??
    eff.flags?.dae?.stacks ??
    eff.getFlag("dae", "stackCount") ??
    eff.flags?.dae?.stackCount ??
    1;

  const next = Math.max(0, Number(cur) - 1);
  if (next > 0) {
    await eff.update({
      "flags.dae.stacks": next,
      "flags.dae.stackCount": next
    });
    ChatMessage.create({ content: `âž– '${eff.label}': ${cur} â†’ ${next} on ${actorDoc.name}.` });
  } else {
    await eff.delete();
    ChatMessage.create({ content: `ðŸ—‘ï¸ '${eff.label}' removed from ${actorDoc.name} (0 stacks).` });
  }
}

