/**
 *
 * On Target or Token
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const ANIMATION1 = 'blfx.spell.melee.heal1.particles1.glow.green';
const SCALE1 = 2; // Size of the animation. Default value: 2
const FADEIN1 = 0;
const FADEOUT1 = 0;
const WAIT_UNTIL_FINISHED1 = -700; // Default value: -700
const WAIT_UNTIL_FINISHED_ENABLED1 = true;
const BELOW_TOKENS1 = false;
const ENABLED1 = false;
const SOUND_ENABLED1 = false;
const SOUND1 = 'blfx.sound.spell.cast.heal.1';
const DELAY_SOUND1 = 0; // Default value: 0
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION2 = 'blfx.spell.range.ray.beam1.sinusoidal.intro.orange';
const WAIT_UNTIL_FINISHED2 = -100; // Default value: -100
const WAIT_UNTIL_FINISHED_ENABLED2 = true;
const ENABLED2 = false;
const SOUND_ENABLED2 = false;
const SOUND2 = 'blfx.sound.spell.cast.generic.1';
const DELAY_SOUND2 = 0; // Default value: 0
const START_TIME_SOUND2 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED2 = false;
const WAIT_UNTIL_FINISHED_SOUND2 = 0;

const ANIMATION3 = 'jb2a.healing_generic.03.burst.green';
const SCALE3 = 2; // Size of the animation. Default value: 2
const FADEIN3 = 0;
const FADEOUT3 = 0;
const BELOW_TOKENS3 = false;
const WAIT_UNTIL_FINISHED3 = -700; // Default value: -700
const WAIT_UNTIL_FINISHED_ENABLED3 = true;
const ENABLED3 = true;
const SOUND_ENABLED3 = true;
const SOUND3 = 'blfx.sound.spell.cast.heal.2';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;
const SHAKE_ENABLED3 = false;
const SHAKE_DURATION3 = 400;
const SHAKE_INTENSITY3 = 0.08;
//
// ------------------------------------------------

if (typeof targetTokens === 'undefined' || targetTokens === null) return;

const controlledToken = sourceToken;
const sourceElevation = controlledToken.document.elevation;

// ON SOURCE TOKEN
await new Sequence()
  .sound()
  .playIf(SOUND_ENABLED1)
  .file(SOUND1)
  .delay(DELAY_SOUND1)
  .startTime(START_TIME_SOUND1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

  .effect()
  .playIf(ENABLED1)
  .file(ANIMATION1)
  .atLocation(controlledToken)
  .scaleToObject(SCALE1)
  .fadeIn(FADEIN1)
  .fadeOut(FADEOUT1)
  .elevation(sourceElevation, { absolute: false })
  .belowTokens(BELOW_TOKENS1)
  .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])
  .play();

for (let targetToken of targetTokens) {
  const targetElevation = targetToken.document?.elevation ?? 0;
  const scaleX = targetToken.document?.texture?.scaleX ?? 1;
  const isTargetFlipped = blfx.helpers.isTokenFlipped(targetToken);

  // FROM SOURCE TO TARGET
  await new Sequence()
    .sound()
    .playIf(SOUND_ENABLED2)
    .file(SOUND2)
    .delay(DELAY_SOUND2)
    .startTime(START_TIME_SOUND2)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED2, WAIT_UNTIL_FINISHED_SOUND2])

    .effect()
    .playIf(ENABLED2)
    .file(ANIMATION2)
    .zIndex(100)
    .elevation(targetElevation)
    .atLocation(controlledToken)
    .stretchTo(targetToken)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED2, WAIT_UNTIL_FINISHED2])

    // ON TARGET
    .sound()
    .playIf(SOUND_ENABLED3)
    .file(SOUND3)
    .delay(DELAY_SOUND3)
    .startTime(START_TIME_SOUND3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

    .effect()
    .playIf(ENABLED3)
    .file(ANIMATION3)
    .zIndex(50)
    .atLocation(targetToken)
    .scaleToObject(SCALE3)
    .fadeIn(FADEIN3)
    .fadeOut(FADEOUT3)
    .elevation(targetElevation, { absolute: false })
    .belowTokens(BELOW_TOKENS3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

    // SHAKE
    .effect()
    .playIf(SHAKE_ENABLED3)
    .copySprite(targetToken)
    .mirrorY(isTargetFlipped)
    .zIndex(0)
    .loopProperty('sprite', 'position.x', {
      from: -SHAKE_INTENSITY3,
      to: SHAKE_INTENSITY3,
      duration: 60,
      pingPong: true,
      delay: 0,
      gridUnits: true,
    })
    .loopProperty('sprite', 'position.y', {
      from: -SHAKE_INTENSITY3,
      to: SHAKE_INTENSITY3,
      duration: 15,
      pingPong: true,
      delay: 0,
      gridUnits: true,
    })
    .scaleToObject(scaleX)
    .duration(SHAKE_DURATION3)
    .play();
}

macro.command:
// Sunfeather Cloak — simple depletion roll (Midi-QoL On Use)
// Authors chat messages as the player who owns the item.
(async () => {
  try {
    const LA = args?.at?.(-1) ?? {};
    const tok = canvas.tokens.get(LA.tokenId ?? "");
    const actor = tok?.actor ?? null;
    const speaker = ChatMessage.getSpeaker({ token: tok });

    // Prefer the active non-GM owner of the actor; fall back to current user
    const OWN = (CONST.DOCUMENT_OWNERSHIP_LEVELS?.OWNER ?? CONST.DOCUMENT_PERMISSION_LEVELS?.OWNER ?? 3);
    const ownerUser =
      game.users.players.find(u => u.active && actor?.testUserPermission?.(u, OWN)) ??
      game.users.players.find(u => actor?.testUserPermission?.(u, OWN)) ??
      game.user;

    const roll = await new Roll("1d6").evaluate({ async: true });
    await roll.toMessage({
      flavor: "Sunfeather Cloak Depletion roll",
      speaker,
      user: ownerUser.id
    });

    if (roll.total === 1) {
      await ChatMessage.create({
        user: ownerUser.id,
        speaker,
        content: `<em>The feathers of the Sunfeather Cloak dim, awaiting the light of the dawn once more.</em>`
      });
    }
  } catch (err) {
    ui.notifications?.error(`Macro error: ${err.message}`);
  }
})();

/**
 * Sunfeather Cloak — depletion die (Midi-QoL onUse, dnd5e 4.x / v12)
 * - Uses CONFIG.Dice.randomUniform() (no Roll hooks, no debugger pauses)
 * - Always posts the d6 result; only consumes on a 1
 * - Decrements Activity uses first (if present), else Item uses
 */
(async () => {
  try {
    const LA = args?.at?.(-1) ?? {};
    const pass = LA.macroPass ?? "onUse";
    if (pass !== "onUse") return true; // run once

    /* ---------- Resolve actor / item / activity robustly ---------- */
    let actor = typeof this !== "undefined" ? this?.actor : undefined;
    let item  = typeof this !== "undefined" ? this?.item  : undefined;

    if (!item && LA.itemUuid) {
      const doc = await fromUuid(LA.itemUuid);
      item = doc?.document ?? doc;
    }
    if (!actor && LA.actorUuid) {
      const adoc = await fromUuid(LA.actorUuid);
      actor = adoc?.document ?? adoc;
    }
    if (!actor && item?.parent?.type === "character") actor = item.parent;
    if (!item && actor && LA.itemId) item = actor.items.get(LA.itemId);

    let tokenObj;
    if (LA.tokenUuid) tokenObj = (await fromUuid(LA.tokenUuid))?.object;
    if (!tokenObj) tokenObj = canvas.tokens.get(LA.tokenId) ?? canvas.tokens.controlled[0] ?? actor?.getActiveTokens?.()[0];

    if (!actor || !item) {
      ui.notifications.warn("Sunfeather: could not resolve actor or item (check ItemMacro + Midi onUse).");
      return true;
    }

    // Identify the dnd5e 4.x Activity if possible
    const actId = LA.activityId ?? LA.activity?.id ?? (() => {
      const u = LA.activityUuid ?? LA.workflowUuid ?? LA.uuid;
      if (!u || typeof u !== "string") return null;
      const m = u.match(/\.Activity\.([^.]+)/);
      return m?.[1] ?? null;
    })();

    const activities = item?.system?.activities ?? {};
    const activity   = actId && activities[actId] ? activities[actId] : null;

    /* ---------- Depletion die (no Roll hooks) ---------- */
    const d6 = 1 + Math.floor(CONFIG.Dice.randomUniform() * 6);

    /* ---------- Consume a use on 1 (activity first, else item) ---------- */
    const getP  = foundry.utils.getProperty;
    const setUp = async (doc, path, newVal) => doc.update({ [path]: newVal });

    let consumed = false;
    let consumedWhere = null;

    if (d6 === 1) {
      // (A) Activity-level uses (dnd5e 4.x)
      if (activity?.uses && Number.isFinite(activity.uses.value)) {
        const path = `system.activities.${actId}.uses.value`;
        const cur  = getP(item, path);
        if (Number.isFinite(cur)) {
          await setUp(item, path, Math.max(0, cur - 1));
          consumed = true;
          consumedWhere = "activity";
        }
      }
      // (B) Item-level uses fallback
      if (!consumed) {
        const path = "system.uses.value";
        const cur  = getP(item, path);
        if (Number.isFinite(cur)) {
          await setUp(item, path, Math.max(0, cur - 1));
          consumed = true;
          consumedWhere = "item";
        }
      }
      if (!consumed) {
        ui.notifications.warn(`"${item.name}" has no configured uses to decrement (activity or item).`);
      }
    }

    /* ---------- Chat output (always shows the roll) ---------- */
    let content = `<div><strong>${item.name}</strong> depletion die: <b>${d6}</b></div>`;
    if (d6 === 1) {
      content += `<div><em>The feathers of the Sunfeather Cloak dim, awaiting the dawn once more.</em></div>`;
      if (consumed) {
        content += `<div><small>(Consumed 1 ${consumedWhere} use.)</small></div>`;
      } else {
        content += `<div><small>(No uses configured—nothing was consumed.)</small></div>`;
      }
    }

    await ChatMessage.create({
      speaker: ChatMessage.getSpeaker({ token: tokenObj }),
      content
      // If you want this private, add: whisper: [game.user.id]
    });

    return true;
  } catch (err) {
    console.error("Sunfeather macro error:", err);
    ui.notifications.error("Sunfeather macro hit an error (see console).");
    return true;
  }
})();

