macroData.command:
/**
 * Midi-QoL ItemMacro (postSave):
 * Pull ALL hostile failed-save targets to the nearest unoccupied space within 15 ft of the caster.
 * Foundry v13 / dnd5e 5.2.x / midi-qol 13.x
 */

const RADIUS_FT = 15;
const ANIMATE = true;

// ---- macro pass guard ----
const lastArg = args?.[args.length - 1] ?? {};
const macroPass = lastArg.macroPass ?? args?.[0]?.macroPass ?? "";
if (macroPass && !["postSave", "postSavesComplete", "savesComplete"].includes(macroPass)) return;

// ---- resolve workflow & caster token ----
const workflow =
  lastArg.workflow ??
  (lastArg.uuid && globalThis.MidiQOL?.Workflow?.getWorkflow ? MidiQOL.Workflow.getWorkflow(lastArg.uuid) : null);

if (!workflow) return ui.notifications?.warn("Pull-in: No workflow found.");

const casterToken =
  workflow.token ??
  (workflow.tokenId ? canvas.tokens.get(workflow.tokenId) : null) ??
  (lastArg.tokenId ? canvas.tokens.get(lastArg.tokenId) : null);

if (!casterToken) return ui.notifications?.warn("Pull-in: No caster token found.");

const gridSize = canvas.grid.size;
const distPerSquare = canvas.scene.grid.distance ?? 5;

// ---- GM mover (supports your MAF old/simple) ----
async function updateTokenAsGM(tokenDocUuid, updates, animate = true) {
  // GM can update directly
  if (game.user.isGM) {
    const doc = await fromUuid(tokenDocUuid);
    if (!doc) return false;
    await doc.update(updates, { animate });
    return true;
  }

  // MAF (old/simple): runAsGM(codeString, argsObj) where code evaluates to function(args)
  if (globalThis.MAF?.runAsGM) {
    const code = `
      return async (args) => {
        const doc = await fromUuid(args.uuid);
        if (!doc) return false;
        await doc.update(args.updates, { animate: args.animate });
        return true;
      };
    `;
    return await globalThis.MAF.runAsGM(code, { uuid: tokenDocUuid, updates, animate });
  }

  ui.notifications?.error("Pull-in: Need GM perms (or MAF.runAsGM) to move hostile tokens.");
  return false;
}

// ---- distance helpers: prefer measurePath (no deprecated measureDistance) ----
function measureCostFt(a, b) {
  try {
    const m = canvas.grid.measurePath([a, b]);
    return m?.cost ?? m?.distance ?? m;
  } catch {
    // last-ditch: euclidean in squares -> feet
    const px = Math.hypot(a.x - b.x, a.y - b.y);
    return (px / gridSize) * distPerSquare;
  }
}

function tokenCentreAtTopLeftPx(token, x, y) {
  const w = (token.document.width ?? 1) * gridSize;
  const h = (token.document.height ?? 1) * gridSize;
  return { x: x + w / 2, y: y + h / 2 };
}

function isHostileRelativeTo(caster, target) {
  const cd = caster.document.disposition ?? 0;
  const td = target.document.disposition ?? 0;
  if (cd === 0) return td === -1;
  return td === -cd;
}

// ---- token-level failed-save extraction (NO actor dedupe) ----
async function toToken(entry) {
  if (!entry) return null;
  if (entry?.document && entry?.center) return entry;               // Token placeable
  if (entry.documentName === "Token") return entry.object ?? canvas.tokens.get(entry.id); // TokenDocument
  if (typeof entry === "string") {                                  // UUID
    const doc = await fromUuid(entry);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const uuid = entry.tokenUuid ?? entry.targetUuid ?? entry.uuid;
  if (uuid) {
    const doc = await fromUuid(uuid);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const id = entry.tokenId ?? entry.targetId ?? entry.id;
  return id ? canvas.tokens.get(id) : null;
}

async function extractFailedTokens() {
  const out = new Map();

  const pools = [
    workflow.failedSaves,
    workflow.failedSave,
    lastArg.failedSaves,
    lastArg.failedSave
  ].filter(Boolean);

  for (const p of pools) {
    const arr = Array.isArray(p) ? p : (p instanceof Set ? [...p] : Object.values(p));
    for (const e of arr) {
      const tok = await toToken(e);
      if (tok?.id) out.set(tok.id, tok);
    }
  }

  // fallback: saveResults array (varies by midi/system versions)
  const sr = workflow.saveResults ?? lastArg.saveResults;
  if (Array.isArray(sr)) {
    for (const r of sr) {
      const failed = (r.failed === true) || (r.passed === false) || (r.save === false) || (r.success === false);
      if (!failed) continue;
      const tok = await toToken(r);
      if (tok?.id) out.set(tok.id, tok);
    }
  }

  return [...out.values()];
}

const failed = (await extractFailedTokens()).filter(t => t?.document);
const targets = failed.filter(t => isHostileRelativeTo(casterToken, t));
if (!targets.length) return;

// ---- occupancy: grid-cell based, footprint-aware, with reservation ----
function cellKey(gx, gy) { return `${gx},${gy}`; }

function cellsForFootprint(topGX, topGY, w, h) {
  const cells = [];
  for (let dx = 0; dx < w; dx++) for (let dy = 0; dy < h; dy++) cells.push(cellKey(topGX + dx, topGY + dy));
  return cells;
}

function topLeftGridOfToken(token) {
  return canvas.grid.getGridPositionFromPixels(token.x, token.y); // [gx, gy]
}

function buildOccupiedSet(excludingTokenIds = new Set()) {
  const occ = new Set();
  for (const t of canvas.tokens.placeables) {
    if (!t?.document) continue;
    if (excludingTokenIds.has(t.id)) continue;

    const [gx, gy] = topLeftGridOfToken(t);
    const w = t.document.width ?? 1;
    const h = t.document.height ?? 1;
    for (const c of cellsForFootprint(gx, gy, w, h)) occ.add(c);
  }
  return occ;
}

function canPlaceAtGrid(token, topGX, topGY, occupied) {
  const w = token.document.width ?? 1;
  const h = token.document.height ?? 1;
  for (const c of cellsForFootprint(topGX, topGY, w, h)) if (occupied.has(c)) return false;
  return true;
}

// ---- direct-path-biased candidate generation (mirrors movement.js approach) ----
// movement.js uses getDirectPath + measurePath(...).cost then getTopLeftPoint :contentReference[oaicite:1]{index=1}
function buildPathBiasedCandidates(caster, target) {
  const origin = { x: caster.x, y: caster.y, w: caster.w, h: caster.h };
  const targ = { x: target.x, y: target.y, w: target.w, h: target.h };

  const originCenter = { x: origin.x + origin.w / 2, y: origin.y + origin.h / 2 };
  const targetCenter = { x: targ.x + targ.w / 2, y: targ.y + targ.h / 2 };

  // For "pull", we want points starting near caster going outward toward the target.
  const path = canvas.grid.getDirectPath([originCenter, targetCenter]) ?? [];
  if (!path.length) return [];

  const cands = [];

  let cost = 0;
  let prev = path[0];

  for (let i = 1; i < path.length; i++) {
    const cur = path[i];

    // Same cost basis as the module: measurePath(...).cost :contentReference[oaicite:2]{index=2}
    cost += (canvas.grid.measurePath([prev, cur])?.cost ?? 0);
    prev = cur;

    if (cost > RADIUS_FT + 1e-6) break;

    // Snap to top-left like the module :contentReference[oaicite:3]{index=3}
    const tl = canvas.grid.getTopLeftPoint(cur);
    cands.push({ x: tl.x, y: tl.y, cost });
  }

  // Sort closest-to-caster first
  cands.sort((a, b) => a.cost - b.cost);
  return cands;
}

// ---- fallback: scan all grid squares within radius, biased toward the target direction ----
function buildRadiusCandidates(caster, target, tokenToPlace) {
  const casterCenter = casterToken.center;
  const targetCenter = target.center;
  const theta = Math.atan2(targetCenter.y - casterCenter.y, targetCenter.x - casterCenter.x);

  const [cgx, cgy] = canvas.grid.getGridPositionFromPixels(caster.x, caster.y);
  const rSquares = Math.ceil(RADIUS_FT / distPerSquare) + Math.max(tokenToPlace.document.width ?? 1, tokenToPlace.document.height ?? 1);

  const out = [];

  for (let gx = cgx - rSquares; gx <= cgx + rSquares; gx++) {
    for (let gy = cgy - rSquares; gy <= cgy + rSquares; gy++) {
      const pt = canvas.grid.getPixelsFromGridPosition(gx, gy); // top-left px
      const center = tokenCentreAtTopLeftPx(tokenToPlace, pt[0] ?? pt.x, pt[1] ?? pt.y);

      const d = measureCostFt(casterCenter, center);
      if (d <= 0.01 || d > RADIUS_FT + 1e-6) continue;

      const thetaCand = Math.atan2(center.y - casterCenter.y, center.x - casterCenter.x);
      const ang = Math.abs(Math.atan2(Math.sin(thetaCand - theta), Math.cos(thetaCand - theta)));

      out.push({
        gx, gy,
        x: pt[0] ?? pt.x,
        y: pt[1] ?? pt.y,
        d,
        ang
      });
    }
  }

  out.sort((a, b) => (a.d - b.d) || (a.ang - b.ang));
  return out;
}

// ---- main placement loop (farthest-first so nobody gets starved) ----
targets.sort((a, b) => measureCostFt(casterToken.center, b.center) - measureCostFt(casterToken.center, a.center));

for (const t of targets) {
  // occupied excluding the token we're moving (so it doesn't block itself while searching)
  const occupied = buildOccupiedSet(new Set([t.id]));

  const w = t.document.width ?? 1;
  const h = t.document.height ?? 1;

  // 1) try along the direct path (module-style)
  let chosen = null;
  const pathCands = buildPathBiasedCandidates(casterToken, t);

  for (const c of pathCands) {
    const [gx, gy] = canvas.grid.getGridPositionFromPixels(c.x, c.y);
    if (!canPlaceAtGrid(t, gx, gy, occupied)) continue;

    chosen = { x: c.x, y: c.y, gx, gy };
    // reserve cells
    for (const key of cellsForFootprint(gx, gy, w, h)) occupied.add(key);
    break;
  }

  // 2) fallback: scan any square within radius, biased toward the target direction
  if (!chosen) {
    const radiusCands = buildRadiusCandidates(casterToken, t, t);
    for (const c of radiusCands) {
      if (!canPlaceAtGrid(t, c.gx, c.gy, occupied)) continue;

      chosen = { x: c.x, y: c.y, gx: c.gx, gy: c.gy };
      for (const key of cellsForFootprint(c.gx, c.gy, w, h)) occupied.add(key);
      break;
    }
  }

  if (!chosen) {
    ui.notifications?.warn(`Pull-in: No free space within ${RADIUS_FT} ft for ${t.name}.`);
    continue;
  }

  await updateTokenAsGM(t.document.uuid, { x: chosen.x, y: chosen.y }, ANIMATE);
}

/**
 * Midi-QoL ItemMacro (postSave):
 * PUSH ALL hostile failed-save targets 15 ft away from the caster.
 * Foundry v13 / dnd5e 5.2.x / midi-qol 13.x
 *
 * Behaviour:
 * 1) Build direct path from caster -> target, step outward using measurePath(...).cost.
 * 2) Prefer the farthest valid space <= 15 ft along that path.
 * 3) Fallback: scan all grid squares within 15 ft of the target, biased away from caster.
 * 4) Footprint-aware occupancy + reservation so multiple targets don't stack.
 * 5) GM-authoritative move via MAF old/simple if needed.
 */

const PUSH_FT = 15;
const ANIMATE = true;

// ---- macro pass guard ----
const lastArg = args?.[args.length - 1] ?? {};
const macroPass = lastArg.macroPass ?? args?.[0]?.macroPass ?? "";
if (macroPass && !["postSave", "postSavesComplete", "savesComplete"].includes(macroPass)) return;

// ---- resolve workflow & caster token ----
const workflow =
  lastArg.workflow ??
  (lastArg.uuid && globalThis.MidiQOL?.Workflow?.getWorkflow ? MidiQOL.Workflow.getWorkflow(lastArg.uuid) : null);

if (!workflow) return ui.notifications?.warn("Push: No workflow found.");

const casterToken =
  workflow.token ??
  (workflow.tokenId ? canvas.tokens.get(workflow.tokenId) : null) ??
  (lastArg.tokenId ? canvas.tokens.get(lastArg.tokenId) : null);

if (!casterToken) return ui.notifications?.warn("Push: No caster token found.");

const gridSize = canvas.grid.size;
const distPerSquare = canvas.scene.grid.distance ?? 5;

// ---- GM mover (supports your MAF old/simple) ----
async function updateTokenAsGM(tokenDocUuid, updates, animate = true) {
  if (game.user.isGM) {
    const doc = await fromUuid(tokenDocUuid);
    if (!doc) return false;
    await doc.update(updates, { animate });
    return true;
  }

  if (globalThis.MAF?.runAsGM) {
    // old/simple MAF: code string evaluates to function(args)
    const code = `
      return async (args) => {
        const doc = await fromUuid(args.uuid);
        if (!doc) return false;
        await doc.update(args.updates, { animate: args.animate });
        return true;
      };
    `;
    return await globalThis.MAF.runAsGM(code, { uuid: tokenDocUuid, updates, animate });
  }

  ui.notifications?.error("Push: Need GM perms (or MAF.runAsGM) to move targets.");
  return false;
}

// ---- distance helpers: prefer measurePath cost ----
function measureCostFt(a, b) {
  try {
    const m = canvas.grid.measurePath([a, b]);
    return m?.cost ?? m?.distance ?? m;
  } catch {
    const px = Math.hypot(a.x - b.x, a.y - b.y);
    return (px / gridSize) * distPerSquare;
  }
}

function tokenCentreAtTopLeftPx(token, x, y) {
  const w = (token.document.width ?? 1) * gridSize;
  const h = (token.document.height ?? 1) * gridSize;
  return { x: x + w / 2, y: y + h / 2 };
}

function isHostileRelativeTo(caster, target) {
  const cd = caster.document.disposition ?? 0;
  const td = target.document.disposition ?? 0;
  if (cd === 0) return td === -1;
  return td === -cd;
}

// ---- token-level failed-save extraction (NO actor dedupe) ----
async function toToken(entry) {
  if (!entry) return null;
  if (entry?.document && entry?.center) return entry;
  if (entry.documentName === "Token") return entry.object ?? canvas.tokens.get(entry.id);
  if (typeof entry === "string") {
    const doc = await fromUuid(entry);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const uuid = entry.tokenUuid ?? entry.targetUuid ?? entry.uuid;
  if (uuid) {
    const doc = await fromUuid(uuid);
    return doc?.object ?? (doc?.id ? canvas.tokens.get(doc.id) : null);
  }
  const id = entry.tokenId ?? entry.targetId ?? entry.id;
  return id ? canvas.tokens.get(id) : null;
}

async function extractFailedTokens() {
  const out = new Map();

  const pools = [
    workflow.failedSaves,
    workflow.failedSave,
    lastArg.failedSaves,
    lastArg.failedSave
  ].filter(Boolean);

  for (const p of pools) {
    const arr = Array.isArray(p) ? p : (p instanceof Set ? [...p] : Object.values(p));
    for (const e of arr) {
      const tok = await toToken(e);
      if (tok?.id) out.set(tok.id, tok);
    }
  }

  const sr = workflow.saveResults ?? lastArg.saveResults;
  if (Array.isArray(sr)) {
    for (const r of sr) {
      const failed = (r.failed === true) || (r.passed === false) || (r.save === false) || (r.success === false);
      if (!failed) continue;
      const tok = await toToken(r);
      if (tok?.id) out.set(tok.id, tok);
    }
  }

  return [...out.values()];
}

const failed = (await extractFailedTokens()).filter(t => t?.document);
const targets = failed.filter(t => isHostileRelativeTo(casterToken, t));
if (!targets.length) return;

// ---- occupancy: grid-cell based, footprint-aware, with reservation ----
function cellKey(gx, gy) { return `${gx},${gy}`; }

function cellsForFootprint(topGX, topGY, w, h) {
  const cells = [];
  for (let dx = 0; dx < w; dx++) for (let dy = 0; dy < h; dy++) cells.push(cellKey(topGX + dx, topGY + dy));
  return cells;
}

function topLeftGridOfToken(token) {
  return canvas.grid.getGridPositionFromPixels(token.x, token.y); // [gx, gy]
}

function buildOccupiedSet(excludingTokenIds = new Set()) {
  const occ = new Set();
  for (const t of canvas.tokens.placeables) {
    if (!t?.document) continue;
    if (excludingTokenIds.has(t.id)) continue;

    const [gx, gy] = topLeftGridOfToken(t);
    const w = t.document.width ?? 1;
    const h = t.document.height ?? 1;
    for (const c of cellsForFootprint(gx, gy, w, h)) occ.add(c);
  }
  return occ;
}

function canPlaceAtGrid(token, topGX, topGY, occupied) {
  const w = token.document.width ?? 1;
  const h = token.document.height ?? 1;
  for (const c of cellsForFootprint(topGX, topGY, w, h)) if (occupied.has(c)) return false;
  return true;
}

// ---- PUSH candidates along direct path (caster -> target), then choose farthest valid ----
function buildPushPathCandidates(caster, target) {
  const origin = { x: caster.x, y: caster.y, w: caster.w, h: caster.h };
  const targ = { x: target.x, y: target.y, w: target.w, h: target.h };

  const originCenter = { x: origin.x + origin.w / 2, y: origin.y + origin.h / 2 };
  const targetCenter = { x: targ.x + targ.w / 2, y: targ.y + targ.h / 2 };

  // Walk along caster -> target direction, but we want to keep going PAST the target direction.
  // We approximate "away from caster" by extending a ray from target in the same direction.
  const theta = Math.atan2(targetCenter.y - originCenter.y, targetCenter.x - originCenter.x);

  // End point far enough (in pixels) to cover at least PUSH_FT in cost terms.
  const pxPerFt = gridSize / distPerSquare;
  const extendFt = PUSH_FT + 10; // buffer
  const end = {
    x: targetCenter.x + Math.cos(theta) * extendFt * pxPerFt,
    y: targetCenter.y + Math.sin(theta) * extendFt * pxPerFt
  };

  const path = canvas.grid.getDirectPath([targetCenter, end]) ?? [];
  if (!path.length) return [];

  const cands = [];
  let cost = 0;
  let prev = path[0];

  for (let i = 1; i < path.length; i++) {
    const cur = path[i];
    cost += (canvas.grid.measurePath([prev, cur])?.cost ?? 0);
    prev = cur;

    if (cost > PUSH_FT + 1e-6) break;

    const tl = canvas.grid.getTopLeftPoint(cur);
    cands.push({ x: tl.x, y: tl.y, cost });
  }

  // For push we want the farthest (closest to 15 ft) first
  cands.sort((a, b) => b.cost - a.cost);
  return cands;
}

// ---- fallback: scan squares within PUSH_FT of current target, biased away from caster ----
function buildPushRadiusCandidates(caster, target, tokenToPlace) {
  const casterCenter = casterToken.center;
  const targetCenter = target.center;

  const thetaAway = Math.atan2(targetCenter.y - casterCenter.y, targetCenter.x - casterCenter.x);

  const [tgx, tgy] = canvas.grid.getGridPositionFromPixels(target.x, target.y);
  const rSquares = Math.ceil(PUSH_FT / distPerSquare) + Math.max(tokenToPlace.document.width ?? 1, tokenToPlace.document.height ?? 1);

  const out = [];

  for (let gx = tgx - rSquares; gx <= tgx + rSquares; gx++) {
    for (let gy = tgy - rSquares; gy <= tgy + rSquares; gy++) {
      const pt = canvas.grid.getPixelsFromGridPosition(gx, gy);
      const x = pt[0] ?? pt.x;
      const y = pt[1] ?? pt.y;

      const centre = tokenCentreAtTopLeftPx(tokenToPlace, x, y);
      const dFromTarget = measureCostFt(targetCenter, centre);
      if (dFromTarget <= 0.01 || dFromTarget > PUSH_FT + 1e-6) continue;

      // Bias: keep candidates in the "away" half-plane (small angle difference)
      const thetaCand = Math.atan2(centre.y - targetCenter.y, centre.x - targetCenter.x);
      const ang = Math.abs(Math.atan2(Math.sin(thetaCand - thetaAway), Math.cos(thetaCand - thetaAway)));

      // Secondary: favour farther (near 15 ft)
      out.push({ gx, gy, x, y, dFromTarget, ang });
    }
  }

  out.sort((a, b) => (b.dFromTarget - a.dFromTarget) || (a.ang - b.ang));
  return out;
}

// ---- main placement loop: nearest-to-caster first for pushes (so close ones don't get blocked) ----
targets.sort((a, b) => measureCostFt(casterToken.center, a.center) - measureCostFt(casterToken.center, b.center));

for (const t of targets) {
  // occupied excluding this token (so it doesn't block itself)
  const occupied = buildOccupiedSet(new Set([t.id]));

  const w = t.document.width ?? 1;
  const h = t.document.height ?? 1;

  let chosen = null;

  // 1) try direct "push ray" candidates (farthest valid <= 15 ft)
  const pathCands = buildPushPathCandidates(casterToken, t);
  for (const c of pathCands) {
    const [gx, gy] = canvas.grid.getGridPositionFromPixels(c.x, c.y);
    if (!canPlaceAtGrid(t, gx, gy, occupied)) continue;

    chosen = { x: c.x, y: c.y, gx, gy };
    for (const k of cellsForFootprint(gx, gy, w, h)) occupied.add(k);
    break;
  }

  // 2) fallback: scan around target within 15 ft, biased away from caster
  if (!chosen) {
    const radiusCands = buildPushRadiusCandidates(casterToken, t, t);
    for (const c of radiusCands) {
      if (!canPlaceAtGrid(t, c.gx, c.gy, occupied)) continue;

      chosen = { x: c.x, y: c.y, gx: c.gx, gy: c.gy };
      for (const k of cellsForFootprint(c.gx, c.gy, w, h)) occupied.add(k);
      break;
    }
  }

  if (!chosen) {
    ui.notifications?.warn(`Push: No free space within ${PUSH_FT} ft for ${t.name}.`);
    continue;
  }

  await updateTokenAsGM(t.document.uuid, { x: chosen.x, y: chosen.y }, ANIMATE);
}

(async () => {
  // =======================
  // CONFIG
  // =======================
  const PICK_RANGE_FT = 30;          // caster may click within this range
  const LAND_RADIUS_FT = 10;         // pack failed targets within this radius of caster landing
  const EXPAND_TO_FT = 30;           // if crowded, expand search up to this radius
  // =======================

  const warn  = (m) => ui.notifications?.warn?.(m)  ?? console.warn(m);
  const error = (m) => ui.notifications?.error?.(m) ?? console.error(m);

  if (!canvas?.ready || !canvas.scene || !canvas.grid) return error("No active canvas/scene.");
  const grid = canvas.grid;
  const scene = canvas.scene;
  const gridSizePx = grid.size;

  // -----------------------
  // Resolve workflow / caster
  // -----------------------
  const a0raw = (typeof args !== "undefined" && args?.length) ? args[0] : {};
  let a0 = a0raw;
  if (typeof a0 === "string") { try { a0 = JSON.parse(a0); } catch { a0 = a0raw; } }

  const wf =
    a0?.workflow ??
    globalThis.workflow ??
    (a0?.uuid && globalThis.MidiQOL?.Workflow?.getWorkflow ? MidiQOL.Workflow.getWorkflow(a0.uuid) : null) ??
    null;

  if (!wf) return error("No Midi-QoL workflow found. Run this as a post-save macro (After Saves Complete).");

  const caster =
    wf.token ??
    (a0?.tokenId ? canvas.tokens.get(a0.tokenId) : null) ??
    canvas.tokens.controlled?.[0] ??
    null;

  if (!caster) return error("Could not resolve caster token.");

  // -----------------------
  // Failed-save tokens
  // -----------------------
  const toTokens = (maybe) => {
    if (!maybe) return [];
    if (maybe instanceof Set) return Array.from(maybe).map(t => t?.object ?? t).filter(Boolean);
    if (Array.isArray(maybe)) return maybe.map(t => t?.object ?? t).filter(Boolean);
    if (typeof maybe === "object") return Object.values(maybe).map(t => t?.object ?? t).filter(Boolean);
    return [];
  };

  let failed = toTokens(wf.failedSaves);
  if (!failed.length) failed = toTokens(wf.failedTargets);
  if (!failed.length && a0?.failedSaves) failed = toTokens(a0.failedSaves);
  if (!failed.length && a0?.failedTargets) failed = toTokens(a0.failedTargets);

  // De-dupe by token id (so two identical monsters both move)
  const uniq = new Map();
  for (const t of failed) uniq.set(t.id, t);
  failed = Array.from(uniq.values());

  // Passengers = caster + failed targets
  const passengersMap = new Map([[caster.id, caster]]);
  for (const t of failed) passengersMap.set(t.id, t);
  const passengers = Array.from(passengersMap.values());

  if (passengers.length <= 1 && !failed.length) return warn("No failed saves – nothing to teleport.");

  // -----------------------
  // Geometry helpers (v13-safe)
  // -----------------------
  const r10 = (n) => Math.round(n * 10) / 10;
  const centreOf = (t) => ({ x: t.x + t.w / 2, y: t.y + t.h / 2 });

  const distFtGrid = (a, b) => {
    const res = grid.measurePath([a, b], { gridSpaces: true });
    return res?.cost ?? Number.POSITIVE_INFINITY;
  };

  const tokenWpx = (tok) => (tok.document?.width ?? 1) * gridSizePx;
  const tokenHpx = (tok) => (tok.document?.height ?? 1) * gridSizePx;

  const inSceneBounds = (tok, x, y) => {
    const dims = scene.dimensions;
    const w = tokenWpx(tok);
    const h = tokenHpx(tok);
    return x >= 0 && y >= 0 && (x + w) <= dims.sceneWidth && (y + h) <= dims.sceneHeight;
  };

  // -----------------------
  // GRID-FOOTPRINT OCCUPANCY (the important fix)
  // -----------------------
  const gridPosFromPixels = (x, y) => {
    if (grid.getGridPositionFromPixels) return grid.getGridPositionFromPixels(x, y);
    // fallback – square grids
    return [Math.floor(x / gridSizePx), Math.floor(y / gridSizePx)];
  };

  const addFootprint = (set, gx, gy, wSquares, hSquares) => {
    for (let dx = 0; dx < wSquares; dx++) {
      for (let dy = 0; dy < hSquares; dy++) {
        set.add(`${gx + dx},${gy + dy}`);
      }
    }
  };

  const movingIds = new Set(passengers.map(t => t.document.id));

  // Occupied cells from ALL non-moving tokens
  const occupiedCells = new Set();
  for (const tok of canvas.tokens.placeables) {
    if (movingIds.has(tok.document.id)) continue;

    const [gx, gy] = gridPosFromPixels(tok.x, tok.y);
    const wSq = tok.document?.width ?? 1;
    const hSq = tok.document?.height ?? 1;
    addFootprint(occupiedCells, gx, gy, wSq, hSq);
  }

  // Reserved cells for our placements (to avoid stacking passengers)
  const reservedCells = new Set();

  const canPlaceAt = (tok, x, y) => {
    if (!inSceneBounds(tok, x, y)) return false;

    const [gx, gy] = gridPosFromPixels(x, y);
    const wSq = tok.document?.width ?? 1;
    const hSq = tok.document?.height ?? 1;

    for (let dx = 0; dx < wSq; dx++) {
      for (let dy = 0; dy < hSq; dy++) {
        const key = `${gx + dx},${gy + dy}`;
        if (occupiedCells.has(key)) return false;
        if (reservedCells.has(key)) return false;
      }
    }
    return true;
  };

  const reserveAt = (tok, x, y) => {
    const [gx, gy] = gridPosFromPixels(x, y);
    const wSq = tok.document?.width ?? 1;
    const hSq = tok.document?.height ?? 1;
    addFootprint(reservedCells, gx, gy, wSq, hSq);
  };

  // -----------------------
  // GM update helper (MAF old/simple supported)
  // -----------------------
  async function updateTokensAsGM(updates) {
    if (!updates?.length) return;

    if (game.user.isGM) {
      return scene.updateEmbeddedDocuments("Token", updates, { animate: false });
    }

    if (globalThis.MAF?.runAsGM) {
      const code = `(args) => {
        const scene = game.scenes.get(args.sceneId);
        if (!scene) return;
        return scene.updateEmbeddedDocuments("Token", args.updates, { animate: false });
      }`;
      return MAF.runAsGM(code, { sceneId: scene.id, updates });
    }

    throw new Error("Not GM and MAF.runAsGM not available.");
  }

  // -----------------------
  // PIXI picker overlay (no timeout)
  // -----------------------
  const RING_COLOUR = 0x50B849;
  const RING_FILL_ALPHA = 0.18;
  const RING_LINE_ALPHA = 0.95;
  const RING_LINE_WIDTH = 3;
  const MARKER_RADIUS = 8;

  function makeOverlay() {
    const overlay = new PIXI.Container();
    overlay.eventMode = "none";
    overlay.sortableChildren = true;

    const ring = new PIXI.Graphics();
    ring.eventMode = "none";

    const marker = new PIXI.Graphics();
    marker.eventMode = "none";

    overlay.addChild(ring);
    overlay.addChild(marker);

    canvas.stage.addChild(overlay);
    return { overlay, ring, marker };
  }

  function drawRing(g, origin, radiusFt) {
    const rPx = (radiusFt / grid.distance) * gridSizePx;
    g.clear();
    g.lineStyle(RING_LINE_WIDTH, RING_COLOUR, RING_LINE_ALPHA);
    g.beginFill(RING_COLOUR, RING_FILL_ALPHA);
    g.drawCircle(origin.x, origin.y, rPx);
    g.endFill();
  }

  function drawMarker(g, p, ok) {
    g.clear();
    g.lineStyle(2, ok ? RING_COLOUR : 0xCC3333, 1);
    g.drawCircle(p.x, p.y, MARKER_RADIUS);
    g.moveTo(p.x - MARKER_RADIUS - 6, p.y);
    g.lineTo(p.x + MARKER_RADIUS + 6, p.y);
    g.moveTo(p.x, p.y - MARKER_RADIUS - 6);
    g.lineTo(p.x, p.y + MARKER_RADIUS + 6);
  }

  const snapCentreFromEvent = (ev) => {
    const pos = canvas.canvasCoordinatesFromClient(ev);
    return grid.getCenterPoint({ x: r10(pos.x), y: r10(pos.y) });
  };

  async function pickDestination(originCentre, rangeFt) {
    const { overlay, ring, marker } = makeOverlay();
    drawRing(ring, originCentre, rangeFt);

    return await new Promise((resolve, reject) => {
      let finished = false;
      let lastSnap = null;

      const cleanup = () => {
        if (finished) return;
        finished = true;

        try { canvas.stage.off("mousemove", onMove); } catch {}
        try { canvas.stage.off("mousedown", onDown); } catch {}
        try { window.removeEventListener("keydown", onKey, true); } catch {}
        try { window.removeEventListener("contextmenu", onCtx, true); } catch {}

        try { canvas.stage.removeChild(overlay); } catch {}
        try { overlay.destroy({ children: true }); } catch {}
      };

      const cancel = () => { cleanup(); reject(new Error("cancelled")); };
      const onKey = (e) => { if (e.key === "Escape") cancel(); };
      const onCtx = (e) => { e.preventDefault(); cancel(); };

      const onMove = (event) => {
        const snap = snapCentreFromEvent(event.data.originalEvent);
        lastSnap = snap;

        const ok = distFtGrid(originCentre, snap) <= (rangeFt + 1e-6);
        drawMarker(marker, snap, ok);
      };

      const onDown = (event) => {
        const oe = event.data.originalEvent;
        if (!oe) return;

        if (oe.button === 2) return cancel();
        if (oe.button !== 0) return;

        const snap = lastSnap ?? snapCentreFromEvent(oe);
        const ok = distFtGrid(originCentre, snap) <= (rangeFt + 1e-6);
        if (!ok) return warn(`Out of bounds – pick within ${rangeFt} ft.`);

        cleanup();
        resolve(snap);
      };

      window.addEventListener("contextmenu", onCtx, true);
      window.addEventListener("keydown", onKey, true);
      canvas.stage.on("mousemove", onMove);
      canvas.stage.on("mousedown", onDown);

      drawMarker(marker, originCentre, true);
    });
  }

  // -----------------------
  // Candidate landing points around caster destination
  // -----------------------
  function* spiralSquares(maxR) {
    yield [0, 0];
    for (let r = 1; r <= maxR; r++) {
      for (let dx = -r; dx <= r; dx++) { yield [dx, -r]; yield [dx, r]; }
      for (let dy = -r + 1; dy <= r - 1; dy++) { yield [-r, dy]; yield [r, dy]; }
    }
  }

  function buildCandidateTopLefts(casterDestTL, casterDestCentre, maxFt) {
    const maxSquares = Math.ceil(maxFt / grid.distance);
    const candidates = [];

    for (const [sx, sy] of spiralSquares(maxSquares)) {
      const x = casterDestTL.x + sx * gridSizePx;
      const y = casterDestTL.y + sy * gridSizePx;

      const centre = grid.getCenterPoint({ x, y });
      const dFt = distFtGrid(casterDestCentre, centre);
      if (dFt <= maxFt + 1e-6) candidates.push({ x, y, dFt });
    }

    candidates.sort((a, b) => a.dFt - b.dFt);
    return candidates;
  }

  // -----------------------
  // PICK DESTINATION
  // -----------------------
  const casterCentre = centreOf(caster);
  let destCentre;
  try {
    destCentre = await pickDestination(casterCentre, PICK_RANGE_FT);
  } catch {
    return;
  }

  const casterDestTL = grid.getTopLeftPoint({ x: r10(destCentre.x), y: r10(destCentre.y) });
  const casterDestCentre = grid.getCenterPoint({ x: casterDestTL.x, y: casterDestTL.y });

  // -----------------------
  // PLACEMENT (now using grid-footprint occupancy)
  // -----------------------
  if (!canPlaceAt(caster, casterDestTL.x, casterDestTL.y)) {
    warn("Caster landing spot is occupied/out of bounds – pick a different point.");
    return;
  }
  reserveAt(caster, casterDestTL.x, casterDestTL.y);

  let candidates = buildCandidateTopLefts(casterDestTL, casterDestCentre, LAND_RADIUS_FT);
  if (failed.length + 1 > candidates.length) {
    candidates = buildCandidateTopLefts(casterDestTL, casterDestCentre, EXPAND_TO_FT);
  }

  const landXY = [];
  landXY.push({ _id: caster.document.id, x: casterDestTL.x, y: casterDestTL.y });

  for (const t of failed) {
    if (t.id === caster.id) continue;

    let placed = false;

    for (const c of candidates) {
      if (c.x === casterDestTL.x && c.y === casterDestTL.y) continue;
      if (!canPlaceAt(t, c.x, c.y)) continue;

      reserveAt(t, c.x, c.y);
      landXY.push({ _id: t.document.id, x: c.x, y: c.y });
      placed = true;
      break;
    }

    if (!placed) warn(`No free landing square found for ${t.name} near the caster.`);
  }

  if (!landXY.length) return warn("Nothing to teleport.");

  // -----------------------
  // BLINK TELEPORT (no animation)
  // Phase 1: alpha 0
  // Phase 2: x/y
  // Phase 3: restore alpha
  // -----------------------
  const originalAlpha = new Map();
  for (const u of landXY) {
    const tok = canvas.tokens.get(u._id);
    const a = tok?.document?.alpha;
    originalAlpha.set(u._id, (typeof a === "number") ? a : 1);
  }

  const fadeOut = landXY.map(u => ({ _id: u._id, alpha: 0 }));
  const fadeIn  = landXY.map(u => ({ _id: u._id, alpha: originalAlpha.get(u._id) ?? 1 }));

  try {
    await updateTokensAsGM(fadeOut);
    await updateTokensAsGM(landXY);
    await updateTokensAsGM(fadeIn);
  } catch (e) {
    console.error(e);
    return error(`Teleport failed: ${e?.message ?? e}`);
  }

  ui.notifications.info(`Teleported caster + ${landXY.length - 1} failed target(s).`);
})();

