/**
 *
 * Teleport Target Token(s)
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const ANIMATION1 = 'blfx.spell.cast.impact.electric.lightning1.color5';
const SCALE1 = 5; // Size of the animation. Default value: 1.5
const WAIT_UNTIL_FINISHED1 = -600; // Default value: -600
const WAIT_UNTIL_FINISHED_ENABLED1 = true;
const ENABLED1 = true;
const SOUND_ENABLED1 = true;
const SOUND1 = 'sounds/Spells/Disappear/spell-teleport-short-4.mp3';
const DELAY_SOUND1 = 0; // Default value: 0
const START_TIME_SOUND1 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED1 = false;
const WAIT_UNTIL_FINISHED_SOUND1 = 0;

const ANIMATION3 = 'blfx.spell.cast.circles2.energy1.smoke.orange';
const SCALE3 = 1.6; // Size of the animation. Default value: 1.5
const FADEIN3 = 500;
const WAIT_UNTIL_FINISHED3 = -900; // Default value: -900
const WAIT_UNTIL_FINISHED_ENABLED3 = true;
const ENABLED3 = false;
const SOUND_ENABLED3 = false;
const SOUND3 = 'blfx.sound.spell.cast.buff.1';
const DELAY_SOUND3 = 0; // Default value: 0
const START_TIME_SOUND3 = 0; // Default value: 0
const WAIT_UNTIL_FINISHED_SOUND_ENABLED3 = false;
const WAIT_UNTIL_FINISHED_SOUND3 = 0;

const ONLY_FRIENDLY3 = true;
const ONLY_5FT_TARGET3 = true;
//
// ------------------------------------------------

if (typeof sourceToken === 'undefined' || sourceToken === null) return;

if (targetTokens.length === 0) {
  ui.notifications.warn('Please select a target first!');
  return;
}

const crosshairTexture = targetTokens.length === 1 ? targetTokens[0].document.texture.src : 'icons/svg/target.svg';

const range = item.system.range?.value ?? 30; // from the item
const targetDest = await Sequencer.Crosshair.show({
  icon: {
    texture: crosshairTexture,
    borderVisible: false,
  },
  location: {
    obj: sourceToken,
    showRange: true,
    wallBehavior: Sequencer.Crosshair.PLACEMENT_RESTRICTIONS.LINE_OF_SIGHT,
    limitMaxRange: range,
    displayRangePoly: false,
  },
});

for (let i = 0; i < targetTokens.length; i++) {
  const targetToken = targetTokens[i];
  if (ONLY_FRIENDLY3 && targetToken.document.disposition !== CONST.TOKEN_DISPOSITIONS.FRIENDLY) {
    ui.notifications.warn('Only friendly targets can be teleported.');
    continue;
  }
  if (ONLY_5FT_TARGET3 && !blfx.helpers.edgesIntersect(sourceToken, targetToken)) {
    ui.notifications.warn('The target must be within 5 feet of you.');
    continue;
  }

  new Sequence()

    // ON SOURCE TOKEN
    .sound()
    .playIf(SOUND_ENABLED1)
    .file(SOUND1)
    .delay(DELAY_SOUND1)
    .startTime(START_TIME_SOUND1)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED1, WAIT_UNTIL_FINISHED_SOUND1])

    .effect()
    .playIf(ENABLED1)
    .file(ANIMATION1)
    .atLocation(sourceToken)
    .scaleToObject(SCALE1)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED1, WAIT_UNTIL_FINISHED1])

    // FADE-OUT
    .animation()
    .on(targetToken)
    .opacity(0)
    .fadeOut(500)
    .waitUntilFinished()

    // TELEPORT
    .animation()
    .on(targetToken)
    .teleportTo(targetDest)
    .closestSquare(i > 0)
    .snapToGrid()
    .waitUntilFinished()

    // ON TARGET
    .sound()
    .playIf(SOUND_ENABLED3)
    .file(SOUND3)
    .delay(DELAY_SOUND3)
    .startTime(START_TIME_SOUND3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_SOUND_ENABLED3, WAIT_UNTIL_FINISHED_SOUND3])

    .effect()
    .playIf(ENABLED3)
    .file(ANIMATION3)
    .atLocation(targetToken)
    .scaleToObject(SCALE3)
    .preset('conditionalWait', [WAIT_UNTIL_FINISHED_ENABLED3, WAIT_UNTIL_FINISHED3])

    // FADE-IN
    .animation()
    .on(targetToken)
    .opacity(1)
    .fadeIn(FADEIN3)
    .waitUntilFinished()

    .play();
}

y9vaIxyrCnHJbYkw.macro:
(async () => {
  /**
   * Midi-QOL OnUse (preambleComplete):
   * Pull each targeted token up to 15 ft closer to the user, landing on the nearest unoccupied spot.
   *
   * Notes:
   * - If a player can’t move NPC tokens, you must run this macro “as GM” OR rely on MAF.runAsGM fallback.
   */

  const PULL_UNITS = 15;              // feet (or whatever your scene units are)
  const SEARCH_RADIUS_SPACES = 3;     // how hard to search for a nearby free square if the ideal landing is occupied
  const USE_MAF_FALLBACK = true;      // uses your MAF.runAsGM if present
  const DEBUG = false;

  const log = (...a) => DEBUG && console.log("PullMacro |", ...a);

  // ---- args[0] normalisation (handles occasional stringified payloads) ----
  const a0raw = args?.[0];
  let a0 = a0raw ?? {};
  if (typeof a0raw === "string") {
    try { a0 = JSON.parse(a0raw); } catch { a0 = {}; }
  }

  // Only fire for Midi OnUse at preambleComplete
  if (a0.tag && a0.tag !== "OnUse") return;
  if (a0.macroPass && a0.macroPass !== "preambleComplete") return;

  // ---- grid helpers (Foundry v13 grid API) ----
  const gridLayer = canvas.grid;
  const baseGrid = gridLayer.grid ?? gridLayer;

  const gridDistance = baseGrid.distance ?? gridLayer.distance ?? canvas.scene?.grid?.distance ?? 5; // units per space
  const stepX = baseGrid.sizeX ?? gridLayer.sizeX ?? gridLayer.size ?? 100;
  const stepY = baseGrid.sizeY ?? gridLayer.sizeY ?? gridLayer.size ?? 100;

  const getOffset =
    gridLayer.getOffset?.bind(gridLayer) ??
    baseGrid.getOffset?.bind(baseGrid);

  const getTopLeftPoint =
    gridLayer.getTopLeftPoint?.bind(gridLayer) ??
    baseGrid.getTopLeftPoint?.bind(baseGrid);

  const measurePath =
    gridLayer.measurePath?.bind(gridLayer) ??
    baseGrid.measurePath?.bind(baseGrid);

  const getDirectPath =
    baseGrid.getDirectPath?.bind(baseGrid) ??
    gridLayer.getDirectPath?.bind(gridLayer); // may not exist on GridLayer; baseGrid is preferred

  if (!getTopLeftPoint || !measurePath) {
    ui.notifications.error("Pull macro: grid measurement API not available.");
    return;
  }

  const distUnits = (p0, p1) => {
    try {
      const res = measurePath([p0, p1]);
      return Number(res?.distance ?? 0);
    } catch {
      // fallback: Euclidean scaled by grid
      const px = Math.hypot((p1.x - p0.x), (p1.y - p0.y));
      const pxPerUnit = (baseGrid.size ?? gridLayer.size ?? 100) / (gridDistance || 5);
      return pxPerUnit ? (px / pxPerUnit) : 0;
    }
  };

  // ---- resolve source token (the user of the item) ----
  const sourcePlaceable =
    (await fromUuid(a0.tokenUuid ?? ""))?.object ??
    canvas.tokens.get(a0.tokenId ?? "");

  if (!sourcePlaceable) {
    ui.notifications.warn("Pull macro: source token not found.");
    return;
  }

  // ---- resolve targets ----
  let targets = [];

  if (Array.isArray(a0.targets) && a0.targets.length) {
    // Midi-QOL docs: args[0].targets is an array of TokenDocuments
    targets = a0.targets.map(td => td?.object).filter(Boolean);
  } else if (Array.isArray(a0.targetUuids) && a0.targetUuids.length) {
    for (const uuid of a0.targetUuids) {
      const td = await fromUuid(uuid);
      const tok = td?.object ?? (td?.document ? td : null);
      if (tok) targets.push(tok);
    }
  } else {
    targets = Array.from(game.user.targets ?? []);
  }

  targets = targets.filter(t => t?.id && t.id !== sourcePlaceable.id);

  if (!targets.length) return;

  // ---- occupancy checks ----
  const reserved = []; // PIXI.Rectangles reserved for already-moved targets this macro run

  const rectAt = (token, x, y) => new PIXI.Rectangle(x, y, token.w, token.h);

  const intersectsAny = (rect, ignoreIds = new Set()) => {
    for (const r of reserved) {
      if (rect.intersects(r)) return true;
    }
    for (const t of canvas.tokens.placeables) {
      if (!t?.id || ignoreIds.has(t.id)) continue;
      const other = new PIXI.Rectangle(t.document.x, t.document.y, t.w, t.h);
      if (rect.intersects(other)) return true;
    }
    return false;
  };

  // Spiral search around a desired top-left point (pixel stepping, grid-snapped).
  const findNearestFreeTopLeft = (token, desiredTopLeft, currentDistToSource) => {
    const ignore = new Set([token.id]); // ignore itself
    const desiredTL = getTopLeftPoint({ x: desiredTopLeft.x, y: desiredTopLeft.y });

    for (let r = 0; r <= SEARCH_RADIUS_SPACES; r++) {
      for (let dx = -r; dx <= r; dx++) {
        for (let dy = -r; dy <= r; dy++) {
          if (Math.max(Math.abs(dx), Math.abs(dy)) !== r) continue; // ring only

          const probe = {
            x: desiredTL.x + dx * stepX,
            y: desiredTL.y + dy * stepY
          };

          const snapped = getTopLeftPoint({ x: probe.x, y: probe.y });
          const testRect = rectAt(token, snapped.x, snapped.y);

          if (intersectsAny(testRect, ignore)) continue;

          // Don’t “pull” someone farther away than they started (allow tiny float wiggle).
          const newCentre = { x: snapped.x + token.w / 2, y: snapped.y + token.h / 2 };
          const newDist = distUnits(newCentre, sourcePlaceable.center);
          if (newDist > currentDistToSource + 0.001) continue;

          return { x: snapped.x, y: snapped.y, rect: testRect };
        }
      }
    }
    return null;
  };

  // ---- movement (player perms often fail on NPCs; GM execution helps) ----
  const moveTokenDocument = async (token, dest) => {
    try {
      await token.document.update({ x: dest.x, y: dest.y });
      return true;
    } catch (err) {
      log("Direct update failed:", err);

      if (USE_MAF_FALLBACK && globalThis.MAF?.runAsGM) {
        try {
          await MAF.runAsGM(
            `(args) => fromUuid(args.uuid).then(d => d.update(args.update))`,
            { uuid: token.document.uuid, update: { x: dest.x, y: dest.y } }
          );
          return true;
        } catch (err2) {
          log("MAF fallback failed:", err2);
        }
      }
      return false;
    }
  };

  // ---- compute pull in grid spaces if possible ----
  const pullSpaces = (gridDistance > 0) ? Math.max(0, Math.floor((PULL_UNITS + 1e-6) / gridDistance)) : 0;

  let moved = 0;

  for (const target of targets) {
    const startDist = distUnits(target.center, sourcePlaceable.center);
    if (startDist <= 0.001) continue;

    // If already adjacent-ish, don’t try to cram into the same space.
    if (startDist <= gridDistance + 0.001) continue;

    let desiredTopLeft = null;

    // Prefer grid-aware stepping via getDirectPath; fall back to proportional “pixel pull”.
    if (pullSpaces > 0 && typeof getDirectPath === "function" && typeof getOffset === "function") {
      const tOff = getOffset(target.center);
      const sOff = getOffset(sourcePlaceable.center);

      const path = getDirectPath([tOff, sOff]) ?? [];
      // Path usually includes the starting space at index 0; stepping N spaces means path[N].
      const step = Math.min(pullSpaces, Math.max(0, path.length - 1));
      const landing = path[step];

      if (landing) {
        const tl = getTopLeftPoint(landing); // accepts GridOffset / Point as coords
        desiredTopLeft = { x: tl.x, y: tl.y };
      }
    }

    if (!desiredTopLeft) {
      // Fallback: pull along straight line by ratio, then snap to grid.
      const dx = sourcePlaceable.center.x - target.center.x;
      const dy = sourcePlaceable.center.y - target.center.y;
      const pxDist = Math.hypot(dx, dy);
      if (pxDist <= 0.001) continue;

      const moveUnits = Math.min(PULL_UNITS, Math.max(0, startDist - gridDistance)); // don’t overlap the source
      const ratio = moveUnits / startDist;

      const approxCentre = { x: target.center.x + dx * ratio, y: target.center.y + dy * ratio };
      const tl = getTopLeftPoint(approxCentre);
      desiredTopLeft = { x: tl.x, y: tl.y };
    }

    // Find nearest unoccupied landing near the desired square.
    const found = findNearestFreeTopLeft(target, desiredTopLeft, startDist);
    if (!found) continue;

    const ok = await moveTokenDocument(target, found);
    if (ok) {
      reserved.push(found.rect);
      moved++;
    }
  }

  if (moved) ui.notifications.info(`Pulled ${moved} target(s).`);
})();
